 
 
 
 
 
 var responsiveImages = {
    globals: {
        breakPoint: 649,
        deviceWidth: screen.width,
        res: "sml"
    },
    detectRes: function() {
        responsiveImages.globals.res = responsiveImages.globals.deviceWidth >= responsiveImages.globals.breakPoint ? "lrg" : "sml"
    },
    init: function() {
        responsiveImages.detectRes();
        $(".responsive").each(function() {
            var e = $(this);
            var t = '<img src="' + e.data(responsiveImages.globals.res) + '" alt="' + e.data("alt") + '" />';
            e.before(t);
            if ($("body").hasClass("node-type-home-page")) {
                var n = '<img class="bglayer" alt="" src="//www.surrey.ac.uk/images/banners/homepage/bannerslides/133440_homepage_banner_text_bglayer_large.png">';
                e.after(n)
            }
        })
    }
};



var carousel = {
    init: function() {
        var e = $(".flexslider").not(".discover-flexslider, .clearing .flexslider");
        e.imagesLoaded(function() {
            carousel.create(e)
        })
    },
    create: function(e) {
        e.each(function() {
            var e = $(this);
            e.removeClass("no-js").addClass("active").flexslider({
                animation: "slide",
                slideshow: false
            });
            var t = e.find(".slides li");
            if (t.length > 0) {
                t.on("click.sml-tray", function() {
                    $(this).toggleClass("active")
                })
            }
        })
    }
};

$(document).ready(function() {
    
    responsiveImages.init();
    
    carousel.init();
    
    
    // flexsliderLoad.init()
});